од ответа не равен 200 или 204 - возвращаем сообщение об ошибке    if($code!=200 && $code!=204)        throw new Exception(isset($errors[$code]) ? $errors[$code] : 'Undescribed error',$code);}catch(Exception $E){    die('Ошибка: '.$E->getMessage().PHP_EOL.'Код ошибки: '.$E->getCode());}/** * Данные получаем в формате JSON, поэтому, для получения читаемых данных, * нам придётся перевести ответ в формат, понятный PHP */$Response=json_decode($out,true);$Response=$Response['response']['leads']['add'];$output='ID добавленных сделок:'.PHP_EOL;foreach($Response as $v)    if(is_array($v))        $output.=$v['id'].PHP_EOL;return $output;?>